;; @@
(defn newtons [f f' x]
  (let [convergent? (fn [{:keys [steps error] :or {steps 0
                                                  error 1}}]
                      (or (< (Math/abs (float error)) 0.00001)
                          (> steps 20)))
        next-guess (fn [{:keys [x last steps] :or {last 0
                                                  steps 0}}]
                     (let [guess (- x (/ (f x)
                                         (f' x)))]
                       {:steps (inc steps)
                        :error (- last guess)
                        :last x
                        :x guess}))]
    (first (drop-while #(not (convergent? %))
                       (iterate #(next-guess %)
                                {:x x})))))
