;;; $$
;;; F(x) = x_0 - \frac{f(x)}{f^\prime (x)}
;;; \;\;\;\;\;\;\;\; 
;;; \Rightarrow
;;; \;\;\;\;\;\;\;\; 
;;; x - \frac{\frac{1}{x} - r}{-\frac{1}{x^2}}
;;; \;\;\;\;\;\;\;\; 
;;; \Rightarrow
;;; \;\;\;\;\;\;\;\; 
;;; x + (\frac{1}{x} - r) x^2
;;; \;\;\;\;\;\;\;\; 
;;; \Rightarrow
;;; \;\;\;\;\;\;\;\; 
;;; x(2 - xr)
;;; $$
;; **

;; @@
(defn reciprocal [r]
  (let [convergent? (fn [{:keys [steps error] :or {steps 0
                                                  error 1}}]
                      (or (< (Math/abs (double error)) 0.00001)
                          (> steps 20)))
        next-guess (fn [{:keys [x last steps] :or {last 0
                                                  steps 0}}]
                     (let [guess (* x (- 2 (* x r)))]
                       {:steps (inc steps)
                        :error (- last guess)
                        :last x
                        :x guess}))
        answer (first (drop-while #(not (convergent? %))
                                  (iterate #(next-guess %)
                                           {:x (if (= 20 r) 0.01 0.1)})))]
    (println answer)
    (:x answer)))
