(defn eulers [f h]
  (fn [[x y]]
    [(+ x h)
     (+ y (* h (f x y)))]))

(defn trapezoidal-rule [f h]
  (fn [[x y]]
    (let [K1 (* h (f x y))
          K2 (* h (f (+ x h) (+ y K1)))]
      [(+ x h)
       (+ y (* 1/2 (+ K1 K2)))])))

(defn simpsons-rule [f h]
  (fn [[x y]]
    (let [K1 (* h (f x y))
          K2 (* h (f (+ x (/ h 2)) (+ y K1)))
          K3 (* h (f (+ x h) (+ y K2)))]
      [(+ x (* h 2))
       (+ y (* 1/3 (+ K1 (* 4 K2) K3)))])))

(defn runge-kutta-4 [f h]
  (fn [[x y]]
    (let [K1 (* h (f x y))
          K2 (* h (f (+ x (/ h 2)) (+ y (/ K1 2))))
          K3 (* h (f (+ x (/ h 2)) (+ y (/ K2 2))))
          K4 (* h (f (+ x h) (+ y K1)))]
      [(+ x h)
       (+ y (* 1/6 (+ K1 (* 2 K2) (* 2 K3) K4)))])))
