;; **
;;; # Rolling Dice, and Flipping Coins
;; **

;; @@
(require '[clojure.math.combinatorics :refer [selections]])
(require '[clojure.pprint :refer [pprint]])
(use 'gorilla-plot.core)
;; @@
;; =>
;;; {"type":"html","content":"<span class='clj-nil'>nil</span>","value":"nil"}
