;; @@
(defn dice [n event-fn]
  {:post [(= 1 (apply + (map second %)))]}
  (->> (map inc (range 6))
       (#(selections % n))
       (map event-fn)
       frequencies
       (#(map (fn [[product freq]]
                (vector product (/ freq (Math/pow 6 n))))
              %))
       sort))
