(defn difference [coin-tosses]
  (->> coin-tosses
       (map #(if (clojure.core/= :H %) 1 -1))
       (apply +)
       ;(#(Math/abs %))
       ))

(def n 3)

(->> (selections [:H :T] n)
     (map difference)
     frequencies
     (#(map (fn [[product freq]]
              (vector product (/ freq (Math/pow 2 n))))
            %))
     sort)

